changeSet.sql:
            CREATE TABLE client(
                id SERIAL PRIMARY KEY,
                name VARCHAR(100) NOT NULL,
                provider INT NOT NULL,
                created TIMESTAMP DEFAULT now()
            );

            CREATE TABLE social_name(
            id SERIAL PRIMARY KEY,
            name VARCHAR(100) NOT NULL,
            created TIMESTAMP DEFAULT now()
            );

            CREATE TABLE email(
                id SERIAL PRIMARY KEY,
                email VARCHAR(100) NOT NULL,
                client INT NOT NULL
            );
